import { useSelector, useDispatch } from "react-redux"
import { CurrentTab } from "../../../redux/general/general"
import { useNavigate } from "react-router-dom"
import { sidebarItems } from "../../../constants/constants"
import { useEffect } from "react"
const Sidebar = () => {
    const currentTab = useSelector(state => state.general)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    useEffect(() => {
        console.log(currentTab)
    })
    return(
        <>
            <div className="sidebar_wrapper position-sticky pt-3 sidebar-sticky">
                <ul className="nav flex-column">
                    {sidebarItems.map((item, index) => (
                        <>
                        <li className={`nav-item ${item.id == currentTab ? "active": ""}`} onClick={() => dispatch(CurrentTab(item.id))}>
                            <span>{item.name}</span>
                        </li>
                        </>
                    ))}

                </ul>
            </div>
        </>
    )
}

export default Sidebar
