import { createSlice } from '@reduxjs/toolkit';
const initialState = {
    sidebar: false
  };


const general = createSlice({
name: 'auth',
initialState,
reducers: {
    Sidebar:(state, action) =>{
        state.sidebar = !state.sidebar
    }
},
});


export const { Sidebar } = general.actions;

export default general.reducer;
